import React, { useState } from "react";
import LoginForm from "./LoginForm";
import ChatForm from "./ChatForm";

function App() {
  const [user, setUser] = useState(null);
  return <div>{user ? <ChatForm user={user} /> : <LoginForm onLogin={setUser} />}</div>;
}

export default App;
